  and flow_step_id = #{flowStepId,jdbcType=VARCHAR}

mapper.delete:
delete from sdb_batch_sub_execution
where trxn_seq = #{trxnSeq,jdbcType=VARCHAR}
  and system_code = #{systemCode,jdbcType=VARCHAR}
  and flow_step_id = #{flowStepId,jdbcType=VARCHAR}

