  is_enabled = #{isEnabled,jdbcType=BIT},
  data_create_user = #{dataCreateUser,jdbcType=VARCHAR},
  data_create_time = #{dataCreateTime,jdbcType=VARCHAR},
  data_update_user = #{dataUpdateUser,jdbcType=VARCHAR},
