mapper.select:
select 

from sdp_datasource
where datasource_id = #{datasourceId,jdbcType=VARCHAR}
  and datasource_type = #{datasourceType,jdbcType=VARCHAR}

mapper.delete:
delete from sdp_datasource
where datasource_id = #{datasourceId,jdbcType=VARCHAR}
  and datasource_type = #{datasourceType,jdbcType=VARCHAR}
